mapper.insert:
		INSERT INTO tbl_board (CATE_CD, TITLE, CONTENT, TAG,
		VIEW_CNT, REG_ID, REG_DT, EDIT_DT)

		VALUES (

		"IMSI"

		, #{title}

		, #{content}

		, #{tag}

		, 0

		, #{reg_id}

		, now()

		, now()

		);
		
		INSERT INTO backup (BID, CATE_CD, TITLE, CONTENT, TAG,
		VIEW_CNT, REG_ID, REG_DT, EDIT_DT)

		VALUES (

		#{bid}

		,"IMSI"

		, #{title}

		, #{content}

		, #{tag}

		, 0

		, #{reg_id}

		, now()

		, now()

		);

mapper.update:
		UPDATE tbl_board SET

		CATE_CD = "IMSI"

		, TITLE = #{title}

		,
		CONTENT = #{content}

		, TAG = #{tag}

		, EDIT_DT = now()

		WHERE

		BID = ${bid};
		
		INSERT INTO backup (BID, CATE_CD, TITLE, CONTENT, TAG,
		VIEW_CNT, REG_ID, REG_DT, EDIT_DT)

		VALUES (

		#{bid}

		,"IMSI"

		, #{title}

		, #{content}

		, #{tag}

		, 0

		, #{reg_id}

		, now()

		, now()

		);
