mapper.insert:
		INSERT INTO TBL_BOARD (CATE_CD, TITLE, CONTENT, TAG,
		VIEW_CNT, REG_ID, REG_DT, EDIT_DT)

		VALUES (

		"IMSI"

		, #{title}

		, #{content}

		, #{tag}

		, 0

		, #{reg_id}

		, now()

		, now()

		)

mapper.update:
		UPDATE TBL_BOARD SET

		CATE_CD = "IMSI"

		, TITLE = #{title}

		,
		CONTENT = #{content}

		, TAG = #{tag}

		, EDIT_DT = now()

		WHERE

		BID = ${bid}
